import React, { useEffect, useState } from 'react';
import { Modal, Button, Col, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

export default function Detail(props) {
    const {
        id_user
    } = props

    const [modal, setModal] = useState(false);
    const [detail, setDetail] = useState({});
    const toggleAndData = () => {
        setModal(!modal);
        fetch("https://reqres.in/api/users/" + id_user)
            .then(res => {
                if (res.status === 200) return res.json();
            })
            .then(resJson => {
                console.log(resJson);
                setDetail(resJson.data)
            });
    }
    const toggle = () => {
        setModal(!modal);
    }

    return (
        <>
            <button className="btn btn-warning" onClick={toggleAndData} title="Detail">
                Detail
            </button>
            <Modal isOpen={modal} toggle={toggle} centered={true}>
                <ModalHeader toggle={toggle}>Detail</ModalHeader>
                <ModalBody>
                    <img src={detail.avatar}></img>
                    <h6>{detail.first_name + " " + detail.last_name}</h6>
                    <h6>{detail.email}</h6>
                </ModalBody>
                <ModalFooter>
                    <Button color="secondary" onClick={toggle}>Close</Button>
                </ModalFooter>
            </Modal>
        </>
    )
}
