import React, { useEffect, useState } from 'react';
import { Modal, Button, Col, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

export default function Detail(props) {
    const {
        id_user
    } = props

    const [modal, setModal] = useState(false);
    const [detail, setDetail] = useState({});
    const toggleAndData = () => {
        setModal(!modal);
        fetch("https://reqres.in/api/users/" + id_user)
            .then(res => {
                if (res.status === 200) return res.json();
            })
            .then(resJson => {
                console.log(resJson);
                setDetail(resJson.data)
            });
    }
    const toggle = () => {
        setModal(!modal);
    }

    return (
        <>
            <button className="btn btn-warning" onClick={toggleAndData} title="Detail">
                Detail
            </button>
            <Modal isOpen={modal} toggle={toggle} centered={true}>
                <ModalHeader toggle={toggle}>Detail {detail.first_name}</ModalHeader>
                <ModalBody>
                    Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" onClick={toggle}>Do Something</Button>{' '}
                    <Button color="secondary" onClick={toggle}>Cancel</Button>
                </ModalFooter>
            </Modal>
        </>
    )
}
