import React, { useEffect, useState } from 'react';
import { Modal, Button, Col } from 'reactstrap';

export default function Detail(props) {
    const {
        id_user,
        user
    } = props

    const [modal, setModal] = useState(false);
    const [detail, setDetail] = useState({});
    const toggleAndData = () => {
        setModal(!modal);
        fetch("https://reqres.in/api/users/" + id_user)
            .then(res => {
                if (res.status === 200) return res.json();
            })
            .then(resJson => {
                console.log(resJson);
                setDetail(resJson.data)
            });
    }
    const toggle = () => {
        setModal(!modal);
    }

    return (
        <>
            <button className="btn btn-warning" onClick={toggleAndData} title="Detail">
                Detail
            </button>
            <Modal isOpen={modal} toggle={toggle} centered={true} size="md">
                <Col md="12" style={{ padding: "0px" }}>
                    <div className="title-popup">
                        <a className="btn btn-std btn-cancel btn-distance" style={{ float: 'right' }} onClick={toggle}> X </a>
                    </div>
                    <div className="title-bg" style={{ fontFamily: "Arial", fontWeight: "bold" }}>
                        <h4 style={{ fontFamily: "Arial", fontWeight: "bold" }}>{user.first_name + " " + user.last_name}</h4>
                    </div>
                </Col>
            </Modal>
        </>
    )
}
